// n8n Function node: Parse $json.text (with smart quotes) and build HTML

// --- Robust parser ---
function parseInput(txt) {
  if (typeof txt === 'object' && txt !== null) return txt;
  if (typeof txt !== 'string') return {};

  let s = String(txt).trim();

  // Normalize Unicode quotes/spaces
  s = s
    .replace(/[\u201C\u201D\u201E\u201F]/g, '"')   // â€œ â€ -> "
    .replace(/[\u2018\u2019\u201A\u201B]/g, "'")  // â€˜ â€™ -> '
    .replace(/[\u00A0\u202F]/g, ' ');             // NBSP, thin space -> space

  // Keep only the outermost {...}
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  if (start !== -1 && end > start) s = s.slice(start, end + 1);

  // Remove trailing commas
  s = s.replace(/,\s*([}\]])/g, '$1');

  try { return JSON.parse(s); } catch (e) {
    // As last resort: replace CRLF escapes that some LLMs leak (optional)
    try { return JSON.parse(s.replace(/\\n/g, ' ')); } catch {}
  }
  return {};
}

// --- Helpers ---
const fmt = (n) => (Number.isFinite(n) ? n.toLocaleString('es-CO') : 'â€”');

// --- Parse source JSON from $json.text ---
const src = parseInput($json.text);

// Safe reads
const p = src.Productos ?? {};
const price = src.Precio ?? {};
const total = src.Total ?? null;
const pago = src.Pago ?? 'â€”';
const pedidoId = 'PED-' + $now.toFormat('yyyyLLddHHmmss') + '-' + Math.random().toString(36).slice(2,6).toUpperCase();
const hora = src.Hora ?? 'â€”';

// Build product list
const items = Object.keys(p).map(k => {
  const qty = p[k];
  const subtotal = price?.[k];
  const line = (subtotal != null)
    ? `${qty} Ã— ${k} â€” ${fmt(subtotal)} pesos`
    : `${qty} Ã— ${k}`;
  return `<li>${line}</li>`;
}).join('\n');

// HTML body
const html = `
<h1>JARVIS FOOD CHAIN</h1>
<p><strong>Nuevo pedido recibido</strong></p>
<p><strong>Pedido:</strong> ${pedidoId}<br/>
<strong>Hora:</strong> ${hora}</p>
<p><strong>Total:</strong> ${fmt(total)} pesos<br/>
<strong>MÃ©todo de pago:</strong> ${pago}</p>
<p><strong>Productos:</strong></p>
<ul>
${items || '<li>(Sin Ã­tems)</li>'}
</ul>
<p><em>Â¿Todo correcto? Si deseas agregar algo mÃ¡s, solo dime. Â¡Gracias por tu pedido!</em></p>
<hr/>
`;

const subject = `ðŸ§¾ Pedido ${pedidoId} â€” Total ${fmt(total)} pesos`;

return [{
  json: {
    ...src,
    subject,
    html
  }
}];

// n8n Function node: Parse $json.text and output a single row for Sheets (Append)

// --- Robust parser (same as above) ---
function parseInput(txt) {
  if (typeof txt === 'object' && txt !== null) return txt;
  if (typeof txt !== 'string') return {};

  let s = String(txt).trim();
  s = s
    .replace(/[\u201C\u201D\u201E\u201F]/g, '"')
    .replace(/[\u2018\u2019\u201A\u201B]/g, "'")
    .replace(/[\u00A0\u202F]/g, ' ');
  const start = s.indexOf('{');
  const end = s.lastIndexOf('}');
  if (start !== -1 && end > start) s = s.slice(start, end + 1);
  s = s.replace(/,\s*([}\]])/g, '$1');

  try { return JSON.parse(s); } catch {
    try { return JSON.parse(s.replace(/\\n/g, ' ')); } catch {}
  }
  return {};
}

const fmtNum = (n) => (Number.isFinite(n) ? n : null);

// --- Parse ---
const src = parseInput($json.text);

const p = src.Productos ?? {};
const price = src.Precio ?? {};

const row = {
  Pedido: 'PED-' + $now.toFormat('yyyyLLddHHmmss') + '-' + Math.random().toString(36).slice(2,6).toUpperCase(),
  Hora: src.Hora ?? null,
  Pago: src.Pago ?? null,
  Total: fmtNum(src.Total) ?? null,
  IVA: fmtNum(src.IVA) ?? null,
};

// Human-readable summary
const pairs = Object.keys(p).map(k => {
  const qty = p[k];
  const subtotal = price?.[k];
  return subtotal != null ? `${k}:${qty} (subtotal ${subtotal})` : `${k}:${qty}`;
});
row.productos_str = pairs.join('; ');

// Dynamic columns per product
for (const k of Object.keys(p)) {
  row[`${k}_qty`] = fmtNum(p[k]);
  if (price?.[k] != null) row[`${k}_subtotal`] = fmtNum(price[k]);
}

// Output one item -> one row
return [{ json: row }];
